content:
.post-header {
  background-size: cover;
  background-position: center;
  background-blend-mode: overlay;
  padding: 50px 20px;
  color: #fff;
  text-align: center;
}
.post-header-content {
  max-width: 800px;
  margin: 0 auto;
}
.post-meta {
  display: flex;
  justify-content: space-between;
  font-size: 14px;
  margin-bottom: 15px;
}
.post-date,
.post-read-time,
.post-views {
  color: #ccc;
}
.post-divider {
  border: 1px solid #fff;
  margin: 20px 0;
}
.post-title {
  font-size: 36px;
  font-weight: bold;
  margin: 0;
}
